 async function loadJSON(path) {try {const response = await fetch(path); if (!response.ok) {throw new Error(`Failed to load ${path}: ${response.statusText}`); }return await response.json(); }catch (error) {console.error(`Error loading ${path}:`, error); return null; }}async function renderPortfolio() {console.log('ğŸš€ Starting portfolio content rendering...'); try {const [aboutData, experienceData, skillsData, certificatesData, educationData, projectsData] = await Promise.all([ loadJSON('/data/about.json'), loadJSON('/data/experience.json'), loadJSON('/data/skills.json'), loadJSON('/data/certificates.json'), loadJSON('/data/education.json'), loadJSON('/data/projects.json') ]); console.log('âœ… All data loaded successfully'); if (aboutData) {const aboutContent = AboutPanel(aboutData); const aboutTextContainer = document.querySelector('#about .about-text'); if (aboutTextContainer) {aboutTextContainer.innerHTML = aboutContent.text; console.log('âœ… About section rendered'); }}if (experienceData && Array.isArray(experienceData)) {const experienceHTML = experienceData.map(exp => ExperienceCard(exp)).join(''); const experienceContainer = document.querySelector('.experience-cards'); if (experienceContainer) {experienceContainer.innerHTML = experienceHTML; console.log(`âœ… Experience section rendered (${experienceData.length}cards)`); }}if (skillsData && Array.isArray(skillsData)) {const skillsHTML = skillsData.map(skill => SkillOrb(skill)).join(''); const skillsContainer = document.querySelector('.skills-galaxy'); if (skillsContainer) {skillsContainer.innerHTML = skillsHTML; console.log(`âœ… Skills section rendered (${skillsData.length}orbs)`); }}if (certificatesData && Array.isArray(certificatesData)) {const certificatesHTML = certificatesData.map(cert => CertificateCard(cert)).join(''); const certificatesContainer = document.querySelector('.cert-stack'); if (certificatesContainer) {certificatesContainer.innerHTML = certificatesHTML; console.log(`âœ… Certificates section rendered (${certificatesData.length}cards)`); }}if (educationData && Array.isArray(educationData)) {const educationHTML = educationData.map(edu => EducationPillar(edu)).join(''); const educationContainer = document.querySelector('.edu-list'); if (educationContainer) {educationContainer.innerHTML = educationHTML; console.log(`âœ… Education section rendered (${educationData.length}items)`); }}if (projectsData && Array.isArray(projectsData)) {const projectsHTML = projectsData.map(project => ProjectCard(project)).join(''); const projectsContainer = document.querySelector('.projects-grid'); if (projectsContainer) {projectsContainer.innerHTML = projectsHTML; console.log(`âœ… Projects section rendered (${projectsData.length}cards)`); reinitializeEffects(); }}console.log('ğŸ‰ Portfolio rendering complete!'); }catch (error) {console.error('âŒ Error rendering portfolio:', error); }}function reinitializeEffects() {const event = new CustomEvent('contentRendered', {detail: {timestamp: Date.now() }}); document.dispatchEvent(event); console.log('ğŸ”„ Effects re-initialized'); }if (document.readyState === 'loading') {document.addEventListener('DOMContentLoaded', renderPortfolio); }else {renderPortfolio(); }if (typeof window !== 'undefined') {window.renderPortfolio = renderPortfolio; window.loadJSON = loadJSON; }