 (function () {function initProjectTilt() {const cards = document.querySelectorAll('.project-card'); if (!cards.length) return; cards.forEach(card => {if (!card.querySelector('.card-glare')) {const glare = document.createElement('div'); glare.classList.add('card-glare'); card.appendChild(glare); }card.style.transformStyle = 'preserve-3d'; card.removeEventListener('mousemove', handleMove); card.removeEventListener('mouseenter', handleEnter); card.removeEventListener('mouseleave', handleLeave); card.removeEventListener('touchstart', handleEnter); card.removeEventListener('touchend', handleLeave); card.addEventListener('mousemove', handleMove); card.addEventListener('mouseenter', handleEnter); card.addEventListener('mouseleave', handleLeave); card.addEventListener('touchstart', handleEnter); card.addEventListener('touchend', handleLeave); }); console.log(`âœ… 3D tilt initialized for ${cards.length}project cards`); }function handleMove(e) {const card = this; const rect = card.getBoundingClientRect(); const cx = rect.left + rect.width / 2; const cy = rect.top + rect.height / 2; const dx = (e.clientX - cx) / rect.width; const dy = (e.clientY - cy) / rect.height; const rotateX = (dy * 20).toFixed(2); const rotateY = (-dx * 20).toFixed(2); const translateZ = Math.min(50, Math.abs((dx + dy) * 50)).toFixed(2); card.style.transform = `perspective(1000px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) translateZ(${translateZ}px)`; card.style.transition = 'transform 100ms ease-out'; const glare = card.querySelector('.card-glare'); if (glare) {const glareX = (dx + 0.5) * 100; const glareY = (dy + 0.5) * 100; glare.style.background = `radial-gradient(circle at ${glareX}% ${glareY}%, rgba(255, 255, 255, 0.3) 0%, rgba(255, 255, 255, 0) 80%)`; glare.style.opacity = '1'; }}function handleEnter() {this.style.willChange = 'transform'; this.style.transition = 'transform 200ms cubic-bezier(0.22,1,0.36,1)'; const glare = this.querySelector('.card-glare'); if (glare) glare.style.opacity = '1'; }function handleLeave() {this.style.transform = 'perspective(1000px) rotateX(0deg) rotateY(0deg) translateZ(0px)'; this.style.transition = 'transform 500ms cubic-bezier(0.22,1,0.36,1)'; this.style.willChange = ''; const glare = this.querySelector('.card-glare'); if (glare) glare.style.opacity = '0'; }if (document.readyState === 'loading') {document.addEventListener('DOMContentLoaded', initProjectTilt); }else {initProjectTilt(); }document.addEventListener('contentRendered', function () {console.log('ðŸ”„ Re-initializing 3D tilt for new content...'); initProjectTilt(); }); if (typeof window !== 'undefined') {window.initProjectTilt = initProjectTilt; }})(); 